func TestGravatarNotFound_LetterAvatarHandler(t *testing.T) {
	RegisterT(t)
	bus.Init(httpclient.Service{})

	server := mock.NewServer()

	user := &entity.User{
		ID:     3,
		Name:   "Darth Vader",
		Email:  "darthvader.fider1234567890@gmail.com",
		Tenant: mock.DemoTenant,
	}

	bus.AddHandler(func(ctx context.Context, q *query.GetUserByID) error {
		if q.UserID == user.ID {
			q.Result = user
			return nil
		}
		return app.ErrNotFound
	})

	code, response := server.
		OnTenant(mock.DemoTenant).
		WithURL("https://demo.test.fider.io/?size=50").
		AddParam("id", user.ID).
		AddParam("name", user.Name).
		Execute(handlers.Gravatar())

	// Note: PNG byte output can vary by platform/Go version, so this test just verifies
	// that a valid PNG is returned (starts with PNG signature) and has correct response code
	Expect(code).Equals(http.StatusOK)
	bytes, _ := io.ReadAll(response.Body)
	// Check PNG signature
	Expect(bytes[:8]).Equals([]byte{137, 80, 78, 71, 13, 10, 26, 10})
	// Verify it ends with PNG IEND chunk
	Expect(bytes[len(bytes)-8:]).Equals([]byte{73, 69, 78, 68, 174, 66, 96, 130})
}

func TestUnknownUser_LetterAvatarHandler(t *testing.T) {
	RegisterT(t)
	bus.Init(httpclient.Service{})

	server := mock.NewServer()
	code, response := server.
		OnTenant(mock.DemoTenant).
		WithURL("https://demo.test.fider.io/?size=50").
		AddParam("id", 0).
		AddParam("name", "Jon Snow").
		Execute(handlers.Gravatar())

	// Note: PNG byte output can vary by platform/Go version, so this test just verifies
	// that a valid PNG is returned (starts with PNG signature) and has correct response code
	Expect(code).Equals(http.StatusOK)
	bytes, _ := io.ReadAll(response.Body)
	// Check PNG signature
	Expect(bytes[:8]).Equals([]byte{137, 80, 78, 71, 13, 10, 26, 10})
	// Verify it ends with PNG IEND chunk
	Expect(bytes[len(bytes)-8:]).Equals([]byte{73, 69, 78, 68, 174, 66, 96, 130})
}
